#instance-1756907414412 {
    background-color: #1a1a4e;
    color: #e0e0e0;
    padding: 15px 15px 15px 15px;
    margin: 10px 10px 10px 10px;
    display: grid;
    grid-template-columns: 1fr;
    gap: 20px;
    animation: fadeIn 0.6s ease-out forwards;
}
#instance-1756907414412 .product-main-image img {
    width: 100%;
    height: auto;
    border-radius: 0.5rem;
    aspect-ratio: 1 / 1;
    object-fit: cover;
    transition: opacity 0.2s ease-in-out;
}
#instance-1756907414412 .product-thumbnails {
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    gap: 10px;
    margin-top: 10px;
}
#instance-1756907414412 .product-thumbnails .thumbnail {
    width: 100%;
    border-radius: 0.375rem;
    cursor: pointer;
    opacity: 0.6;
    transition: all 0.2s;
    border: 2px solid transparent;
}
#instance-1756907414412 .product-thumbnails .thumbnail:hover, #instance-1756907414412 .product-thumbnails .thumbnail.active {
    opacity: 1;
    border-color: var(--interactive-primary, #6366f1);
    transform: scale(1.05);
}
#instance-1756907414412 .product-title { 
    font-size: 25.6px;
    font-weight: 700;
}
#instance-1756907414412 .product-description { 
    margin: 0.75rem 0;
    font-size: 14.4px;
}
