#instance-1756907414412 .product-price { font-size: 2.25rem; font-weight: 800; color: #22c55e; }
#instance-1756907414412 .product-compare-price { font-size: 1.25rem; text-decoration: line-through; color: var(--text-muted, #a1a1aa); }
#instance-1756907414412 .product-actions { margin-bottom: 2rem; }
#instance-1756907414412 .whatsapp-btn {
    background-color: #25D366;
    color: #ffffff;
    padding: 0.75rem 1.5rem;
    border-radius: 9999px;
    text-decoration: none;
    font-weight: 600;
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    transition: transform 0.2s, box-shadow 0.2s;
    border: none;
    cursor: pointer;
}
#instance-1756907414412 .whatsapp-btn:hover { 
    transform: scale(1.05);
    box-shadow: 0 0 15px #25D366;
}
#instance-1756907414412 .order-form {
    background-color: rgba(26, 26, 78, 0.7);
    padding: 1.5rem;
    border-radius: 0.75rem;
    display: flex;
    flex-direction: column;
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0px 4px 15px 0px rgba(0, 0, 0, 0.3);
    transition: all 0.3s ease;
}
